import axios from 'axios';
import React, { useState } from 'react';
import URL from '../../Connection';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

function Login({ setisLogged }) {

    const [login, setLogin] = useState({});


    function handleChange(e) {

        console.log(e.target.name, e.target.value);
        setLogin({
            ...login,
            [e.target.name]: e.target.value
        })
    }

    const handleSubmit = async () => {
        try {
            const response = await axios.post(`${URL}/User/login/`, login)
            if (response.status == 200) {
                setisLogged(true);
                toast("Wow you are logged in!");

            }


        } catch (error) {
            console.log(error);
        }
    }

    const style = {
        "display": "flex",
        "flexDirection": "column",
    "justifyContent": "center",
    "alignItems": "center",
    "border": "1px solid #ccc",
    "borderRadius": "5px",
    "margin": "auto",
    "padding": "10px",
    "width": "650px"
}

return (
    <div className="container-fluid m-3 p-3" style={style}>
        <div className="row ">
            <div className="col">

                <div className="mb-3">
                    <label htmlFor="exampleInputEmail1" className="form-label">Email address</label>
                    <input type="email" name='email' onChange={handleChange} className="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" />

                </div>
                <div className="mb-3">
                    <label htmlFor="exampleInputPassword1" className="form-label">Password</label>
                    <input type="password" name='password' onChange={handleChange} className="form-control" id="exampleInputPassword1" />
                </div>

                <button type="submit" className="btn btn-primary" onClick={handleSubmit}>Submit</button>

            </div>
        </div>
    </div>
)
}

export default Login
