import axios from "axios";
import React, { useState } from "react";
import URL from "../../Connection";
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import { useNavigate } from "react-router-dom";

function Login({ setisLogged }) {
  const [login, setLogin] = useState({});
  const [user, setUser] = useState("Patient")
  const navigate = useNavigate();

  function handleChange(e) {
    // console.log(e.target.name, e.target.value);
    setLogin({
      ...login,
      [e.target.name]: e.target.value,
    });
  }
  const email = login.email;
  const password = login.password;
  const handleSubmit = async () => {
    try {
      const response = await axios.post(`${URL}/clinic-staff/api/login`, {
        email,
        password,
      });
      if (response.status === 200) {
        setisLogged(true);
        navigate("/finddoctor");
        toast.success("You are logged in!");

        // console.log(response.data);
      } else {
        toast.error("Something went wrong");
      }
    } catch (error) {
      console.log(error);
      toast.error("Something Went Wrong :(");
    }
  };

  const onOptionChange = e => {
    setUser(e.target.value)
  }

  return (
    <div className="container-fluid login m-5 p-5">
      <div className="row ">
        <div>
          <h1>Login</h1>
          <input type="radio" name="user" checked={user === "Patient"} onChange={onOptionChange}/>
          <label htmlFor="patient">Patient</label>

          <input type="radio" name="user"   checked={user === "Staff"}onChange={onOptionChange}/>
          <label htmlFor="staff">Staff</label>
          <div className="mb-3">
            <label htmlFor="exampleInputEmail1" className="form-label">
              Email address
            </label>
            <input
              type="email"
              name="email"
              onChange={handleChange}
              className="form-control"
              id="exampleInputEmail1"
              aria-describedby="emailHelp"
            />
          </div>
          <div className="mb-3">
            <label htmlFor="exampleInputPassword1" className="form-label">
              Password
            </label>
            <input
              type="password"
              name="password"
              onChange={handleChange}
              className="form-control"
              id="exampleInputPassword1"
            />
          </div>

          <button
            type="submit"
            className="btn btn-primary"
            onClick={handleSubmit}
          >
            Submit
          </button>
          <ToastContainer />
        </div>
      </div>
    </div>
  );
}

export default Login;
