import Carousel from './Carousel'
import './styles-medicines.css'
import MedicineProducts from './MedicineProducts'
import Consultation from './Consultation'
import React, { useEffect, useState } from "react";
import LocationSelector from "./LocationSelector";
import SearchBar from "./SearchBar";
import PopularSearches from "./PopularSearches";
import { Link, NavLink } from "react-router-dom";
import axios from "axios";
import { ToastContainer, toast } from "react-toastify";

import URL from "../../Connection";



const Medicines = () => {

  const [medicines, setMedicines] = useState([]);

  useEffect(() => {
    const getMedicines = async () => {
      try {
        const response = await axios.get(`${URL}/medicine/api/all`);
        if (!response.data) {
          toast.error("Something went wrong");
        } else {
          setMedicines(response.data);
        }

      } catch (error) {
        toast.error("Something went wrong");
        console.log(error);

      }
    };
    getMedicines();
  }, []);

  return (
    <div>

<div className="container my-3">
        <div className="row"  >
          <div className="col"  >
            {medicines.map((doc, index) => (
              <div key={index} className="card  mx-3">
                {/* <img src="..." className="card-img-top" alt="..." /> */}
                <div className="card-body " >
                  <h2 className="card-title">{doc.name}</h2>
                  <h5 className="card-title">{doc.specialty}</h5>
                  <h6 className="card-title">{doc.education}</h6>
                  <h6 className="card-title">{doc.contactInfo}</h6>
                  {/* <h5 className="card-title">{doc.dateOfBirth}</h5> */}

                  <p className="card-text">
                    {doc.availableHours}
                  </p>
                  <p>{doc.medicalHistory}</p>
                  <NavLink
                    to={{
                      pathname: "/doctorProfile",
                      state:doc
                    }}
                    className="btn btn-primary"
                  >
                    show profile
                  </NavLink>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>

      {/* <section> 
        <form className="d-flex m-5 col-8 justify-content-center" role="search">
        <input className="form-control me-2 " type="search" placeholder="Search" aria-label="Search" />
        <button class="btn btn-outline-info" type="submit">Search</button>
      </form></section>
      <Carousel />
      <MedicineProducts />
      <Consultation />
      <div></div> */}
    </div>
  )
}

export default Medicines
